import styled from "styled-components"

// CSS que estavam dentro do APP.JS
export const Logo = styled.img`  
  margin-left: 15px;
  width: 50px;
`

export const Container = styled.div`
  display: flex;
  flex-direction: column;
`

export const Titulo = styled.span`
  font-weight: bold;
  color: #ffffff;
  font-size: 1.5rem;  
`

export const Main = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  justify-content: center;
  align-items: center;
`
export const Imagem = styled.img`
  justify-content: center;
  align-items: center;
  width: 500px;
`

export const Button = styled.button`
  width: 250px;
  background-color: #f05b00;
  border: none;
  width: 150px;
  height: 40px;
  border-radius: 10px;
`
export const MainButton = styled.div`
  display: flex;
  gap: 10px;
`

export const Card = styled.div `
    display: flex;
    flex-direction: column;
    align-items: center;
    box-shadow: 2px 2px 5px darkgray;
    text-align: center;
    border: 1px solid black;
    margin: 1.5rem;
    background-color: #edc4a1;
    `

export const GoCart = styled.img`  
margin-right: 15px;
width: 50px;
`
export const HeaderNinja = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
`;
export const InputDiv = styled.div`
  padding-right: 120px;
`;

export const InputBusca = styled.input`
  width: 300px;
  height: 35px;
`;

export const MainServicos = styled.div`
  display: flex;
  flex-direction: row;
  width: 100%;

`
// Fim dos CSS que estavam no APP.JS
