import React, { Component } from "react";
import styled from "styled-components";
// import { Cabecalho } from "../components/Header";
import ninja from "../assets/ninja.png";
import ninjaMain from "../assets/ninja_main.png";
import ImgCarrinho from "../assets/cart.png";
import {
  Container,
  Logo,
  Titulo,
  Main,
  MainButton,
  Imagem,
  Button,
  Card,
  GoCart,
} from "./estilos";
import CardJobs from "../components/CardJobs/CardJobs";

const Cabecalho = styled.div`
  background-color: #f05b00;
  height: 4em;
  width: 100%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;
const HeaderNinja = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
`;
const InputDiv = styled.div`
  padding-right: 120px;
`;

const InputBusca = styled.input`
  width: 300px;
  height: 35px;
`;

export default class Telaservicos extends Component {
  state = {
    telaAtual: "servicos",
    listaDeProdutos:[
      {id: "1", name:"FullSatck", validade: "30/10/2022", preco: "R$3.000,00" },
      {id: "2", name:"FrontEnd", validade: "20/09/2022", preco: "R$1.000,00" },
      {id: "3", name:"BackEnd", validade: "25/08/2022", preco: "R$2.000,00" },
    ]
  };

  render() {
    const mapProduto = this.state.listaDeProdutos
    .map((produto) =>{
      return(
      <CardJobs
        id={produto.id}
        name={produto.name}
        validade={produto.validade}
        preco={produto.preco}
      />
      )
    })

    return (
      <div>
        <Cabecalho>
          <HeaderNinja>
            <Logo src={ninja} onClick={this.props.goToInicial}></Logo>
            <Titulo>LabeNinjas</Titulo>
          </HeaderNinja>
          <InputDiv>
            <InputBusca placeholder="Busca"></InputBusca>
          </InputDiv>
          <div>
            <GoCart
              src={ImgCarrinho}
              onClick={this.props.goToCarrinho}
            ></GoCart>
          </div>
        </Cabecalho>
        <Main>
          <h2> Estamos na página de serviços </h2>
          <hr />
          <button onClick={this.props.goToInicial}>Tela Inicial</button>
        </Main>
        <div>
        {mapProduto}
          </div>
      </div>
    );
  }
}
