import React, { Component } from 'react'
import styled from 'styled-components';
import ImgCart from '../assets/carrinho-cart.png'

const Card = styled.div `
    display: flex;
    flex-direction: column;
    align-items: center;
    box-shadow: 2px 2px 5px darkgray;
    text-align: center;
    border: 1px solid black;
    margin: 0.5rem;
    width: fit-content;

    `
const BotoesCard = styled.div`
display: flex;
flex-direction: row;
width:100%;
justify-content: space-between;
`
    
const CarrinhoCard = styled.img`

`

export default class CardJobs extends Component {

  render() {
    return (
      <Card>
        <p>{this.props.name}</p>
        <p>Até {this.props.validade} por R${this.props.preco},00</p>
        <BotoesCard>
        <button>Detalhes</button>
        <CarrinhoCard src={ImgCart}></CarrinhoCard>
        </BotoesCard>
        
      </Card>
      
    )
  }
}
