import React, { Component } from "react";
import styled from "styled-components";
// import { Cabecalho } from "../components/Header";
import ninja from "../assets/ninja.png";
import ninjaMain from "../assets/ninja_main.png";
import ImgCarrinho from "../assets/cart.png"
import {
  Container,
  Logo,
  Titulo,
  Main,
  MainButton,
  Imagem,
  Button,
  Card,
  GoCart
} from "./estilos";

const Cabecalho = styled.div`
background-color: #f05b00;
height: 4em;
display: flex;
flex-direction: row;
align-items: center;
justify-content: space-between;
`

const HeaderNinja = styled.div`
display: flex;
flex-direction: row;
align-items: center;
justify-content: center;
`

const HeaderInput = styled.div`
margin
`

export default class Telaservicos extends Component {
  state = {
    telaAtual: "servicos",
  };

  render() {
    return (
      <div>
        <Cabecalho>
          <HeaderNinja>
          <Logo src={ninja} onClick={this.props.goToInicial}></Logo>
          <Titulo>LabeNinjas</Titulo>
          </HeaderNinja>
          <HeaderInput>
          <input></input>
          </HeaderInput>
          <div>
          <GoCart src={ImgCarrinho}></GoCart>
          </div>
          
        </Cabecalho>
        <Main>
          <Card>
           
            <p>{this.props.name}</p>
            <p>R${this.props.preco},00</p>
            <button
              onClick={() =>
                this.props.adiciona(
                  this.props.id,
                  this.props.name,
                  this.props.preco
                )
              }
            >
              Adicionar
            </button>
          </Card>
          <button onClick={this.props.goToInicial}>Tela Inicial</button>
        </Main>
      </div>
    );
  }
}
