import React, { Component } from 'react'
import styled from 'styled-components'
import {Cabecalho} from './components/Header'
import ninja from './assets/ninja.png'
import ninjaMain from './assets/ninja_main.png'

const Logo = styled.img`  
  margin-left: 15px;
  width: 50px;
`
const Container = styled.div`
  display: flex;
  flex-direction: column;
  `

const Titulo = styled.span`
  font-weight: bold;
  color: #ffffff;
  font-size: 1.5rem;  
`

const Main = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  justify-content: center;
  align-items: center;
`
const Imagem = styled.img`
  justify-content: center;
  align-items: center;
  width: 500px;
`

const Button = styled.button`
  width: 250px;
  background-color: #f05b00;
  border: none;
  width: 150px;
  height: 40px;
  border-radius: 10px;
`

const MainButton = styled.div`
  display: flex;
  gap: 10px;

`
export default class App extends Component {

  state = {
    currentPage: "home",
    cart: [],
    inputSearch: "",
    selectedJobId: ""
  }

choosePage = () => {
    switch (this.state.currentPage) {
      case "home":
        return <HomePage changePage={this.changePage} />
      case "servicos":
        return <ServicesListPage searchText={this.state.inputSearch} changePage={this.changePage} addToCart={this.addToCart} cart={this.state.cart} goToDetailPage={this.goToDetailPage}/>
      case "cadastro":
        return <CreateServicePage changePage={this.changePage} />
      default:
        return <HomePage changePage={this.changePage} />
    }
  }


  render() {
    return (
      <Container>
        <Cabecalho>
          <Logo src={ninja}></Logo>
          <Titulo>LabeNinjas</Titulo>
        </Cabecalho>

        <Main>
          <Imagem src={ninjaMain}></Imagem>
          <MainButton>
          <Button onClick={this.state.cadastro}>Ir para Cadastro</Button>
          <Button onClick={this.state.servicos}>Ir para Servi√ßos</Button>
          </MainButton>
        </Main>



      </Container>
    )
  }
}
