import React, { Component } from 'react'
import styled from 'styled-components'
import {Cabecalho} from './components/Header'
import ninja from './assets/ninja.png'
import ninjaMain from './assets/ninja_main.png'

const Logo = styled.img`  
  margin-left: 15px;
  width: 50px;
`

const Titulo = styled.span`
  font-weight: bold;
  color: #ffffff;
  font-size: 1.5rem;  
`

const Main = styled.div`
  display: flex;
  width: 100%;
  justify-content: center;
  align-items: center;
`

const Imagem = styled.img`
  justify-content: center;
  align-items: center;
  width: 500px;
`
const Container = styled.div`
  display: flex;
`


export default class App extends Component {

  /*choosePage = () => {
    switch (this.state.currentPage) {
      case "home":
        return <HomePage changePage={this.changePage} />
      case "list":
        return <ServicesListPage searchText={this.state.inputSearch} changePage={this.changePage} addToCart={this.addToCart} cart={this.state.cart} goToDetailPage={this.goToDetailPage}/>
      case "form":
        return <CreateServicePage changePage={this.changePage} />
      case "cart":
        return <CartPage changePage={this.changePage} cart={this.state.cart} removeFromCart={this.removeFromCart} clearCart={this.clearCart}/>
      case "detail":
        return <ServiceDetail jobId={this.state.selectedJobId} changePage={this.changePage} addToCart={this.addToCart} cart={this.state.cart}/>
      default:
        return <HomePage changePage={this.changePage} />
    }
  }*/ 


  render() {
    return (
      <div>
        <Cabecalho>
          <Logo src={ninja}></Logo>
          <Titulo>LabeNinjas</Titulo>
        </Cabecalho>

        <Main>
          <Imagem src={ninjaMain}></Imagem>
        </Main>
      </div>
    )
  }
}
