import React, { Component } from "react";
import styled from "styled-components";
// import { Cabecalho } from "../components/Header";
import ninja from "../assets/ninja.png";
import ninjaMain from "../assets/ninja_main.png";
import ImgCarrinho from "../assets/cart.png";
import {
  Logo,
  Titulo,
  MainServicos,
  GoCart,
  HeaderNinja,
  InputDiv,
  InputBusca,
} from "./estilos";
import CardJobs from "../components/CardJobs";

const Cabecalho = styled.div`
  background-color: #f05b00;
  height: 4em;
  width: 100%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;

const Filtros = styled.div`
display: flex;
justify-content: center;
`

export default class Telaservicos extends Component {
  state = {
    telaAtual: "servicos",
    listaDeProdutos: [
      {
        id: "1",
        name: "FullSatck",
        prazo: "30/10/2022",
        preco: "R$3.000,00",
      },
      {
        id: "2",
        name: "FrontEnd",
        prazo: "20/09/2022",
        preco: "R$1.000,00",
      },
      { id: "3", name: "BackEnd", prazo: "25/08/2022", preco: "R$2.000,00" },
    ],
  };

  render() {
    const mapProduto = this.state.listaDeProdutos.map((produto) => {
      return (
        <CardJobs
          id={produto.id}
          name={produto.name}
          prazo={produto.prazo}
          preco={produto.preco}
        />
      );
    });

    return (
      <div>
        <Cabecalho>
          <HeaderNinja>
            <Logo src={ninja} onClick={this.props.goToInicial}></Logo>
            <Titulo>LabeNinjas</Titulo>
          </HeaderNinja>
          <InputDiv>
            <InputBusca placeholder="Busca"></InputBusca>
          </InputDiv>
          <div>
            <GoCart
              src={ImgCarrinho}
              onClick={this.props.goToCarrinho}
            ></GoCart>
          </div>
        </Cabecalho>
        <Filtros>
          <select
          placeholder="Ordenar por"
          >
            <option value="Preço Crescente">Preço Crescente</option>
            <option value="Preço Decrescente">Preço Decrescente</option>
            <option value="Job">Job</option>
            <option value="Prazo">Prazo</option>
          </select>
          <input placeholder="Valor Minimo"></input>
          <input placeholder="Valor Maximo"></input>
          </Filtros>
        <MainServicos>
        <div>{mapProduto}</div>
        
        </MainServicos>
       </div>
    );
  }
}
